shaders.treeShader = love.graphics.newShader("extern float time;\nvarying vec4 vpos;\n \n#ifdef VERTEX\n\tvec4 position(mat4 transform_projection, vec4 vertex_position)\n\t{\n\t\tvpos = vertex_position;\n\t\t\n\t\tvertex_position.x = vertex_position.x - cos(vertex_position.x * 0.01 + time) * 2.0; \n\t\tvertex_position.y = vertex_position.y - sin(vertex_position.y * 0.01 + time) * 0.3; \n\t\treturn transform_projection * vertex_position;\n\t}\n#endif\n\n#ifdef PIXEL\nvec4 effect( vec4 color, Image texture, vec2 texture_coords, vec2 screen_coords)\n{\n    vec4 texcolor = Texel(texture, texture_coords);\n    return texcolor * color;\n}\n#endif\n")
